import React, { Component } from 'react';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';

import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import FaceRecognition from './components/FaceRecognition/FaceRecognition'
import './App.css';

const app= new Clarifai.App({
  apiKey:'4e38471215dc409ba857c6b92b3aab20'
});
const particalOptions={
  particles:{
    number:{
      value:100,
      density:{
        enable:true,
        value_area:800
      }
    }
  }                
}

class App extends Component {
  constructor() {
    super();
    this.state = {
      input: "",
      imageUrl: "",
      box:{}
    };
  }

  calculateFaceLocation=(data)=>{
    const clariaiFace= data.outputs[0].data.regions[0].region_info.bounding_box;
    const image=document.getElementById('inputImage');
    const wdth = Number(image.width);
    const hgt= Number(image.height);
    //console.log(wdth, hgt);
    return{
      leftCol:clariaiFace.left_col * wdth,
      topRow:clariaiFace.top_row * hgt,
      rightCol: wdth-(clariaiFace.right_col*wdth),
      bottomRow: hgt-(clariaiFace.bottom_row*hgt)
    }
  }
  displayFaceBox=(box)=>{
   // console.log(box);
    this.setState({box: box});
  }

  onInputChange = (event) => {
    this.setState({ input: event.target.value });
  };

  onButtonSubmit = () => {
    this.setState({ imageUrl: this.state.input });    
    app.models
      .predict(
        Clarifai.FACE_DETECT_MODEL,
        this.state.input
      )
      .then(response =>this.displayFaceBox(this.calculateFaceLocation(response))) 
      .catch(err => console.log(err));       
  };

  render() {
    return (
      <div className="App">
        <Particles className="particles" params={particalOptions} />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm
          onInputChange={this.onInputChange}
          onButtonSubmit={this.onButtonSubmit}
        />
        <FaceRecognition box={this.state.box} imageUrl={this.state.imageUrl} />
      </div>
    );
  }
}

export default App;
